mapper.select:
select*
 from(
    SELECT ROWNUM AS rn, a.*
 	from(select m.*, s.s_name, o.deal_date , o.go_startdate , o.go_enddate
         from gym_order o, member m, g_s s
         where o.m_number=m.m_number
         and o.s_number=s.s_number
         and o.g_id = s.g_id
         and o.g_id = #{g_id}
         and o.go_isend= 0
         order by o.go_enddate asc)a
    )
 where rn BETWEEN #{start} AND #{end}
